import ProjectSlider from './../../components/projectSlider';
import { useState } from 'react';
import React, {useEffect, useRef} from "react";
import {ReactComponent as QuoteRight} from './../../assets/images/quote-right.svg';
import {ReactComponent as QuoteLeft} from './../../assets/images/quote-left.svg';
import { gsap } from "gsap";
import { useNavigate } from 'react-router-dom';

import './home.scss'
import { ReactComponent as ReactImg } from './../../assets/images/react.svg'
import { ReactComponent as AngularImg } from './../../assets/images/angular.svg'
import { ReactComponent as NodeImg } from './../../assets/images/node.svg'
import { ReactComponent as MongoImg } from './../../assets/images/mongo.svg'
import { ReactComponent as JavascriptImg } from './../../assets/images/javascript.svg'
import { ReactComponent as FigmaImg } from './../../assets/images/figma.svg'
import { ReactComponent as SassImg } from './../../assets/images/sass.svg'
import { ReactComponent as TypescriptImg } from './../../assets/images/typescript.svg'
import { ReactComponent as SolidityImg } from './../../assets/images/solidity.svg'
import { ReactComponent as PolygonImg } from './../../assets/images/polygon.svg'

const Data = [
    {
        id: 0,
        img: "https://ik.imagekit.io/onyedika/phoneapp_oXM1Z9FA_.png?ik-sdk-version=javascript-1.4.3&updatedAt=1667104369642",
    },
    {
        id: 1,
        img: "https://ik.imagekit.io/onyedika/slide/pipar-beta?ik-sdk-version=javascript-1.4.3&updatedAt=1664831519274",
    },
    {
        id: 2,
        img: "https://ik.imagekit.io/onyedika/slide/pipar_peinACEKO.png?ik-sdk-version=javascript-1.4.3&updatedAt=1651255256618",
    },
    {
        id: 3,
        img: "https://ik.imagekit.io/onyedika/slide/calender_S6GI-SE7_.png?ik-sdk-version=javascript-1.4.3&updatedAt=1651255256123",
    },
    {
        id: 4,
        img: "https://ik.imagekit.io/onyedika/slide/movie_Si8QWOouP.png?ik-sdk-version=javascript-1.4.3&updatedAt=1651255256396",
    },
    {
        id: 5,
        img: "https://ik.imagekit.io/onyedika/slide/natour_QGZwpr7Ta.png?ik-sdk-version=javascript-1.4.3&updatedAt=1651255256510",
    },
    {
        id: 6,
        img: "https://ik.imagekit.io/onyedika/slide/ncehr_GV14iEwWG.png?ik-sdk-version=javascript-1.4.3&updatedAt=1651255256179",
    },
    {
        id: 7,
        img: "https://ik.imagekit.io/onyedika/slide/guvve_sUg2If5kc.png?ik-sdk-version=javascript-1.4.3&updatedAt=1651255256276",
    },
    {
        id: 8,
        img: "https://ik.imagekit.io/onyedika/slide/web3-forum_2lXPIycqd.png?ik-sdk-version=javascript-1.4.3&updatedAt=1651255256840",
    },
];


function Home() {
    const navigate = useNavigate();
    const Illus = useRef();
    const i = gsap.utils.selector(Illus);
    const tlm = useRef();
    const dlm = useRef();

    // useEffect(() => {
    //     dlm.current = gsap.timeline()
    //     .fromTo(i(".c-tween"), {scale: 0.7}, {
    //         scale: 1, repeat: -1, yoyo: true,
    //       })
    // });

    useEffect(() => {
        tlm.current = gsap.timeline()
        .fromTo(i(".b-one"), {fill: "#1E2128", opacity: 0, scale: 0.7}, {
            fill: "#FF605C", opacity: 1, scale: 1.3, duration: 1, ease: "none", transformOrigin: 'center center', stagger: 0.1, repeat: -1, yoyo: true
          })
        .fromTo(i(".b-two"), {fill: "#1E2128", opacity: 0, scale: 0.7}, {
            fill: "#FFBD44", opacity: 1, scale: 1.3, duration: 1, ease: "none", transformOrigin: 'center center', stagger: 0.1, repeat: -1, yoyo: true
          })
        .fromTo(i(".b-three"), {fill: "#1E2128", opacity: 0, scale: 0.7}, {
            fill: "#00CA4E", opacity: 1, scale: 1.3, duration: 1, ease: "none", transformOrigin: 'center center', stagger: 0.1, repeat: -1, yoyo: true
          })
        .fromTo(i(".a-one"), {opacity: 0}, {
            opacity: 1, duration: 1, ease: "none", transformOrigin: 'center center', repeat: -1, yoyo: true
          })
        .fromTo(i(".a-two"), {opacity: 0}, {
            opacity: 1, duration: 1, ease: "none", transformOrigin: 'center center', repeat: -1, yoyo: true
          })
        .fromTo(i(".a-three"), {opacity: 0}, {
            opacity: 1, duration: 1, ease: "none", transformOrigin: 'center center', repeat: -1, yoyo: true
          })
        .fromTo(i(".a-four"), {opacity: 0}, {
            opacity: 1, duration: 1, ease: "none", transformOrigin: 'center center', repeat: -1, yoyo: true
          })
        .fromTo(i(".a-five"), {opacity: 0}, {
            opacity: 1, duration: 1, ease: "none", transformOrigin: 'center center', repeat: -1, yoyo: true
          })
        .fromTo(i(".a-six"), {opacity: 0}, {
            opacity: 1, duration: 1, ease: "none", transformOrigin: 'center center', repeat: -1, yoyo: true
          })
        .fromTo(i(".a-seven"), {opacity: 0}, {
            opacity: 1, duration: 1, ease: "none", transformOrigin: 'center center', repeat: -1, yoyo: true
          })
        .fromTo(i(".a-eight"), {opacity: 0}, {
            opacity: 1, duration: 1, ease: "none", transformOrigin: 'center center', repeat: -1, yoyo: true
          })
        .fromTo(i(".a-nine"), {opacity: 0}, {
            opacity: 1, duration: 1, ease: "none", transformOrigin: 'center center', repeat: -1, yoyo: true
          })
    });

    const [background, setBackground] = useState('');

    const image = Data.filter(data => data.id === background).map(filteredData => (
        filteredData.img
    ))

    const nav = () => {
        return navigate('/work'), window.location.reload();

      }

    const Styles = {
       backgroundImage: `url(${image})`
    }

    const hiddenElements = document.querySelectorAll('.hidden');

    const observer = new IntersectionObserver((entries) => {
        entries.forEach((entry) => {
            if (entry.isIntersecting){
                entry.target.classList.add('show');
            } else {
                entry.target.classList.remove('show');
            }
        })
    })

    hiddenElements.forEach((el) => observer.observe(el))

    return (
        <div className="home">
            <div className="home-intro hidden">
                <div className="content">
                <div className="content-tertiary">
                    <p><span></span> Hi there! I'm Hassan Tariq</p>
                </div>
                <div className="content-primary">
                    <h1>a <span>Frontend Engineer</span>. I help startups <span>launch</span> and <span>grow</span> their products</h1>
                </div>
                <div className="content-secondary">
                    <p>I am a software engineer with more than four years of experience. Recognized as a practical and effective developer, experienced in leading cross-functional teams in a time-pressured setting to complete projects on schedule and within budget.</p>
                </div>
                </div>

                <div className="hero-icon">
                <svg xmlns="http://www.w3.org/2000/svg" width="200" height="100">
  <defs>
    <linearGradient id="myGradient" x1="0%" y1="0%" x2="100%" y2="0%">
      <stop offset="0%" style="stop-color:#4b6cb7;stop-opacity:1" />
      <stop offset="100%" style="stop-color:#182848;stop-opacity:1" />
    </linearGradient>
  </defs>
  <rect width="100%" height="100%" fill="url(#myGradient)" />
</svg>
                </div>
            </div>
            <div className="tooling hidden">
                <div className="tooling-title"><h2>Tooling</h2></div>
                <div className="tooling-box">
                <div className="box-content">
                <div className="box-icon"><ReactImg className="icon" /></div>
                <div className="box-text"><p>React</p></div>
                </div>
                <div className="box-content">
                <div className="box-icon"><AngularImg className="icon" /></div>
                <div className="box-text"><p>Angular</p></div>
                </div>
                <div className="box-content">
                <div className="box-icon"><NodeImg className="icon" /></div>
                <div className="box-text"><p>Node</p></div>
                </div>
                <div className="box-content">
                <div className="box-icon"><MongoImg className="icon" /></div>
                <div className="box-text"><p>MongoDB</p></div>
                </div>
                <div className="box-content">
                <div className="box-icon"><JavascriptImg className="icon" /></div>
                <div className="box-text"><p>Javascript</p></div>
                </div>
                <div className="box-content">
                <div className="box-icon"><FigmaImg className="icon" /></div>
                <div className="box-text"><p>Figma</p></div>
                </div>
                <div className="box-content">
                <div className="box-icon"><SassImg className="icon" /></div>
                <div className="box-text"><p>Sass</p></div>
                </div>
                <div className="box-content">
                <div className="box-icon"><TypescriptImg className="icon" /></div>
                <div className="box-text"><p>Typescript</p></div>
                </div>
                <div className="box-content">
                <div className="box-icon"><SolidityImg className="icon" /></div>
                <div className="box-text"><p>Solidity</p></div>
                </div>
                <div className="box-content">
                <div className="box-icon"><PolygonImg className="icon" /></div>
                <div className="box-text"><p>Polygon</p></div>
                </div>
                </div>
            </div>
            <div className="showroom-section hidden">
            <div className="showroom-title"><h2>Showroom</h2></div>
            <div className="showroom">
                <div className="showroom-background" style={Styles}></div>
                <ProjectSlider setBackground={setBackground} />
            </div>
            <div class="button-wrap">
            <button class="butt" onClick={nav}>All Project details</button>
            </div>
            </div>
            <div className="featured hidden">
                <div className="featured-title"><h2>Featured</h2></div>
                <div className="featured-box">
                    <div className="article">
                        <div className="top">
                            <p>Publisher: <span>Hasankhankor</span></p>
                        </div>
                        <div className="bottom">
                            <h4>Revolutionize Your Content Strategy with All Video Downloader App</h4>
                            <div className="info">
                                <p>Sept 15, 2024</p>
                                <a href="https://www.linkedin.com/posts/hasan-khankor-038795199_videodownloader-contentstrategy-digitalmarketing-activity-7155147223297601538-fVca?utm_source=share&utm_medium=member_desktop">
                                <svg fill="white"></svg>
    </a>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
            <div className="quote hidden">
            <div className="quote-body">
                <QuoteLeft className="left" />
            <div className="quote-body-content">
                <p>Creativity is not just about invention, but also about curation. Pulling things from the internet and pasting them into your project is like weaving a tapestry of innovation, stitching together ideas to create something entirely new.<span>!</span></p>
            </div>
            <QuoteRight className="right" />
            </div>
            </div>
        </div>
    )
}

export default Home
